windowclass.script:
function onInit()
	super.onInit();
	OptionsManager.unregisterCallback("SHPC", super.updateHealthDisplay);
	OptionsManager.unregisterCallback("SHNPC", super.updateHealthDisplay);
	OptionsManager.registerCallback("SHPC", updateHealthDisplay);
	OptionsManager.registerCallback("SHNPC", updateHealthDisplay);
	updateHealthDisplay();
	label_temp.setVisible(false);
end

function updateHealthDisplay()
	super.updateHealthDisplay();
	label_temp.setVisible(false);
end
